package main

import (
	"fmt"

	"github.com/francescoalemanno/cryptipass"
)

func main() {
	cp := cryptipass.NewInstanceFromList(custom_list)
	avgH := 0.0
	tot := 0.0
	for i := range 200000 {
		W, H := cp.GenFromPattern("W")
		avgH += H
		tot += 1
		if i < 10 {
			fmt.Println(W, H)
		}
	}
	avgH /= tot
	fmt.Println("On average this list produces ", avgH, "bits entropy per word")

}

